mapper.update:
update tb_theme_class
set enabled = 'D' 
where id = #{id,jdbcType=INTEGER}

mapper.select:
select 

from tb_theme_class
where enabled in ('0','1','R') and parent_id = #{themeId,jdbcType=INTEGER}

select 

from tb_theme_class
where enabled in ('0','1') and parent_id is null and update_opr = #{userId,jdbcType=INTEGER}

select count(1) CNT 
from tb_theme_class t 
where t.parent_id is null and enabled in ('0','1') and update_opr = #{userId,jdbcType=INTEGER};
