mapper.select:
select 

from tb_theme_class
where name = #{name,jdbcType=VARCHAR} and update_opr=#{oprId,jdbcType=INTEGER} 

    and parent_id = #{parentId,jdbcType=INTEGER}


    and parent_id is null

mapper.update:
update tb_theme_class
set enabled = #{status,jdbcType=CHAR} 
where id = #{id,jdbcType=INTEGER}

mapper.select:
select 

from tb_theme_class
where enabled in ('0','1','R') and parent_id = #{themeId,jdbcType=INTEGER}

select 

from tb_theme_class
where enabled in ('0','1') and parent_id is null and update_opr = #{userId,jdbcType=INTEGER}

select count(1) CNT 
from tb_theme_class t 
where t.parent_id is null and t.enabled in ('0','1') and t.update_opr = #{userId,jdbcType=INTEGER};
